INSERT INTO `abouts` (`id`, `about`, `created_at`, `updated_at`) VALUES
(1, 'SBLF (Strategic Business Leader Forum) adalah sebuah forum bisnis interaktif yang dirancang khusus untuk membawa para pemimpin bisnis, pengusaha, dan profesional industri untuk bersama dalam sebuah networking efektif untuk mengatasi tantangan global, regional, dan nasional. Bersama mengedepankan isu isu penting, menyertakan strategi breakthrough yang penting dalam resiliensi bisnis melalui catalytic collaboration, leadership and corporate energy, strategic game changer business.', '2024-04-03 11:39:42', '2024-04-03 11:39:42');
