INSERT INTO `misis` (`id`, `misi`, `created_at`, `updated_at`) VALUES
(1, 'Menghadirkan perspektif terkini mengenai integrasi\nteknologi dalam berbagai aspek bisnis untuk\nmeningkatkan efisiensi, produktivitas, dan daya saing.', '2024-04-03 11:47:31', '2024-04-03 11:47:31'),
(2, 'Mendorong strategi implementasi yang breakthrough, dan\nstrategi bisnis yang borientasi pada game changing.', '2024-04-03 11:47:43', '2024-04-03 11:47:43'),
(3, 'Mendorong kolaborasi antara pelaku bisnis dan pakar\nteknologi untuk menciptakan solusi inovasi yang dapat\nmemecahkan tantangan bisnis yang kompleks.', '2024-04-03 11:47:57', '2024-04-03 11:47:57'),
(4, 'Mewadahi pertukaran ide, gagasan, pengalaman, dan\nbest practice antar anggota untuk memperluas\nnetworking profesional dan membangun kemitraan\nstrategis.', '2024-04-03 11:48:11', '2024-04-03 11:48:11'),
(5, 'Menginspirasi peserta untuk mengadopsi pendekatan\nberbasis teknologi dalam pengambilan keputusan bisnis,\nserta memberikan dampak positif bagi masyarakat dan\nlingkungan.', '2024-04-03 11:48:27', '2024-04-03 11:48:27');
INSERT INTO `opportunities` (`id`, `image`, `judul`, `deskripsi`, `created_at`, `updated_at`) VALUES
(1, 'Kfb9pMARlvh8HcxXEqfALRrcXLVgrXoD7lvTDqOX.png', 'Pengembangan Jaringan', 'Forum bisnis memberikan\r\nkesempatan untuk berkoneksi\r\ndengan sesama pengusaha,\r\ninvestor, mentor, dan ahli industri.\r\nStart-up dapat memperluas\r\njaringan bisnisnya, mencari mitra\r\nkerja, pelanggan potensial, serta\r\npemasok melalui keanggotaan\r\nyang mutualisme.', '2024-04-03 11:53:11', '2024-04-04 00:21:03'),
(2, 'G5eVVaGEmt8m8uu3j0dthBfqVYasUmWAjevt64pT.png', 'Berbagi Pengetahuan dan Pembelajaran', 'Pengadaan pidato utama, diskusi panel, dan lokakarya\r\nyang membahas berbagai aspek strategi breakthrough.\r\nMemberikan meaningful insight dari berbagai pengusaha\r\nberpengalaman dan pemimpin industri, dalam mengatasi\r\ntantangan global, regional, dan nasional.', '2024-04-03 11:53:55', '2024-04-04 00:21:17'),
(3, 'k3MSozsmB9kvEH9EEA22HEHAzYlujlKAufRT8Dbn.png', 'Akses ke Pendanaan dan Sumber Daya', 'Investor Pitching\r\nAkses dalam Investment\r\nMemberi ruang dalam ide dan inovasi kepada\r\ninvestor potensial, dalam meningkatkan peluang\r\nuntuk mendapatkan pendanaan dalam bisnis.', '2024-04-03 11:54:24', '2024-04-04 00:21:29');
