INSERT INTO `visis` (`id`, `visi`, `created_at`, `updated_at`) VALUES
(1, 'Menjadi panggung inspiratif di mana\nkolaborasi menjadi katalisator, strategi\ninovatif menjadi problem solver, dan\nresiliensi korporasi menjadi energi.\nMerangkul tinggi ide dan gagasan\nsebagai game changer, dan\nmembangun keberlanjutan melalui\ncatalytic collaboration untuk\nmenciptakan masa depan bisnis yang\npenuh gemilang.', '2024-04-03 11:47:10', '2024-04-03 11:47:10');
